mapper.select:
/* SELECT getMenuTreeList */ WITH RECURSIVE CTS AS ( SELECT h.TREE_ID ,h.PARENT_ID ,h.MENU_LVL ,h.USER_LVL ,h.MENU_ID ,h.MENU_NM ,h.MENU_DESC ,h.SORT_SEQ ,h.MENU_TYPE ,h.MENU_URL ,h.MENU_ICON ,h.USE_YN ,CAST(SORT_SEQ AS CHAR(100)) lvl FROM wcsdb.TB_COM_MENU_MST h WHERE PARENT_ID = '' UNION ALL SELECT b.TREE_ID ,b.PARENT_ID ,b.MENU_LVL ,b.USER_LVL ,b.MENU_ID ,b.MENU_NM ,b.MENU_DESC ,b.SORT_SEQ ,b.MENU_TYPE ,b.MENU_URL ,b.MENU_ICON ,b.USE_YN ,CONCAT(c.lvl, '', LPAD(b.SORT_SEQ, 2, '0') ) lvl FROM wcsdb.TB_COM_MENU_MST b INNER JOIN CTS c ON b.PARENT_ID = c.TREE_ID )
SELECT CONCAT(REPEAT(' ', k.MENU_LVL), 'ã„´', k.MENU_ID) AS menuId ,k.MENU_NM AS menuNm ,k.MENU_DESC AS menuDesc ,k.MENU_LVL AS menuLvl ,k.USER_LVL AS userLvl ,k.SORT_SEQ AS sortSeq ,k.MENU_TYPE AS menuType ,k.MENU_URL AS menuUrl ,k.MENU_ICON AS menuIcon ,k.TREE_ID AS treeId ,k.PARENT_ID AS parentId ,k.USE_YN AS useYn ,CAST(RPAD(k.lvl, 10, '0') AS unsigned) AS orderLvl FROM CTS k WHERE 1 = 1
AND k.MENU_ID IN
#{item.menuId}
ORDER BY orderLvl ;

/* SELECT getMenuUnpagedList */
WITH RECURSIVE CTS AS ( SELECT h.TREE_ID ,h.PARENT_ID ,h.MENU_ID FROM wcsdb.TB_COM_MENU_MST h WHERE 1 = 1
AND MENU_ID LIKE CONCAT('%', #{menuId}, '%')
AND MENU_NM LIKE CONCAT('%', #{menuNm}, '%')
AND USE_YN = #{useYn}
UNION ALL SELECT b.TREE_ID ,b.PARENT_ID ,b.MENU_ID FROM wcsdb.TB_COM_MENU_MST b INNER JOIN CTS c ON b.TREE_ID = c.PARENT_ID )
SELECT a.MENU_ID ,a.MENU_NM ,a.MENU_DESC ,a.MENU_LVL ,a.SORT_SEQ ,a.USER_LVL ,a.MENU_TYPE ,a.USE_YN ,a.MENU_URL ,a.MENU_ICON ,a.MAIN_SHOW_ICON ,a.TREE_ID ,a.PARENT_ID ,a.REG_DT FROM wcsdb.TB_COM_MENU_MST a
WHERE a.MENU_ID IN (SELECT k.MENU_ID FROM CTS k GROUP BY k.MENU_ID)
ORDER BY a.SORT_SEQ ;

/* SELECT getMenuUserLvlList */
SELECT USER_LVL
FROM wcsdb.TB_COM_MENU_MST a
WHERE a.PARENT_ID = #{parentId}
  AND USER_LVL IS NOT NULL
GROUP BY USER_LVL
ORDER BY USER_LVL DESC
;

/* SELECT getMenuExcelTreeList */
WITH RECURSIVE CTS AS ( SELECT h.TREE_ID ,h.PARENT_ID ,h.MENU_ID FROM wcsdb.TB_COM_MENU_MST h WHERE 1 = 1
AND MENU_ID LIKE CONCAT('%', #{menuId}, '%')
AND MENU_NM LIKE CONCAT('%', #{menuNm}, '%')
AND USE_YN = #{useYn}
UNION ALL SELECT b.TREE_ID ,b.PARENT_ID ,b.MENU_ID FROM wcsdb.TB_COM_MENU_MST b INNER JOIN CTS c ON b.TREE_ID = c.PARENT_ID )
SELECT MENU_ID AS menuId FROM CTS k GROUP BY MENU_ID;
